sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	//var theFirstValue = g_form.getValue('u_choice_1');
	g_form.clearOptions('u_choice_2');
	switch (newValue){
		case 'HR':
			g_form.addOption('u_choice_2', 'HR1', 'HR1');
			g_form.addOption('u_choice_2', 'HR2', 'HR2');
			g_form.addOption('u_choice_2', 'HR3', 'HR3');
			g_form.addOption('u_choice_2', 'Other', 'Other');
			break;
		case'Facilities' :
			g_form.addOption('u_choice_2', 'Facilities1', 'Facilities1');
			g_form.addOption('u_choice_2', 'Facilities2', 'Facilities2');
			g_form.addOption('u_choice_2', 'Facilities3', 'Facilities3');
			g_form.addOption('u_choice_2', 'Other', 'Other');
			break;
		case 'Legal':
			g_form.addOption('u_choice_2', 'Legal1', 'Legal1');
			g_form.addOption('u_choice_2', 'Legal2', 'Legal2');
			g_form.addOption('u_choice_2', 'Legal3', 'Legal3');
			g_form.addOption('u_choice_2', 'Other', 'Other');
			break;
		case '': g_form.clearOptions('u_choice_2');
				break;
	}

   //Type appropriate comment here, and begin script below

}
